import { Stack, Input, Button } from "@chakra-ui/react";
import React from "react";

interface IProps{

}
interface PostFormProps {
   plate?: string;
}

class PostForm extends React.Component<IProps,PostFormProps> {

    constructor(props:IProps){
        super(props);
        this.state = {
            plate: ''
        };
    }

    onSubmit = (e : React.ChangeEvent<HTMLFormElement>) => {
        e.preventDefault()
        alert(this.state.plate)
    }

    changeHandler = (e : React.ChangeEvent<HTMLInputElement>) => {
        console.log(e.target.name)
        console.log(e.target.value)
        this.setState({[e.target.name]: e.target.value})

    }
    render () {
        const {plate} = this.state
        return (
            <div>
                <label alignSelf='left'> Submit a request to Reserve space </label> 
                <form onSubmit = {this.onSubmit}>
                    <Stack spacing={3}>
                        <Input variant="outline" placeholder="Plate" name='plate' width="500px" alignSelf="left" value={plate} onChange={this.changeHandler} marginLeft="20px"/>
                        <Input variant="outline" placeholder="Name" width="500px" alignSelf="ter" />
                        <Button type="submit" colorScheme="teal" variant="outline" width="100px" alignSelf="center">
                         Submit
                        </Button>
                    </Stack>
                </form>
            </div>
        )
    }
}

export default PostForm;
