import { Table, TableCaption, Thead, Tr, Th, Tbody, Td, Select, Input, Box} from "@chakra-ui/react";
import React, { useEffect, useState } from "react";

export interface DataTableProps{

}

const DataTable: React.FC<DataTableProps> = ()=> {

    const [spaces,updateSpaces] = useState([]);
    const [reservations,updateReservations] = useState([]);

    useEffect( ()=> {
        const getData = async () => {
            const resp = await fetch('http://127.0.0.1:3000/api/spaces');
            const json = await resp.json();
            console.log(json)
            updateSpaces(json)
        }
        getData();
    }, []);

    useEffect( ()=> {
        const getData = async () => {
            const resp = await fetch('http://127.0.0.1:3000/api/reservations');
            const json = await resp.json();
            console.log(json)
            updateReservations(json)
        }
        getData();
    }, []);



    return ( 
        <div>
            <Table size="sm" variant="striped" colorScheme="teal">
            <TableCaption textAlign="center">Current reservations</TableCaption>
            <Thead>
            <Tr>
                <Th>Spot</Th>
                <Th>Car Plate</Th>
                <Th>Entry time</Th>
                <Th>State</Th>
            </Tr>
            </Thead>
            <Tbody>
            {
                reservations.map((object:any, i) => {
                    return (
                        <Tr>
                            <Td>{object.spaceId}</Td>
                            <Td>{object.placa}</Td>
                            <Td>{object.hour}</Td>
                            <Td>taken</Td>
                        </Tr>
                    )
                })

            }
            {
                spaces.map((object:any, i) => {
                    return (
                        <Tr>
                            <Td>{object.id}</Td>
                            <Td>{object.placa? object.placa:'-'}</Td>
                            <Td>{object.hour? object.placa:'-'}</Td>
                            <Td>{object.estado}</Td>
                        </Tr>
                    )
                })

            }
            </Tbody>
        </Table>
        <Box className='rowC' marginLeft="700px">
        <Input placeholder="Search" width="250px"/>
        <Select placeholder="Filter by" size="sm" alignSelf="Right" width="120px">
            <option value="option1">Plate</option>
        </Select>
        </Box>
      </div>



    );
}
export default DataTable;
