import { Stack, Input, Button } from "@chakra-ui/react";
import React from "react";

interface IProps{

}
interface PostFormProps {
   plate?: string;
}

class PostForm extends React.Component<IProps,PostFormProps> {

    constructor(props:IProps){
        super(props);
        this.state = {
            plate: '123'
        };
    }

    onSubmit(){
        alert('PSP')
    }

    changeHandler = (e : React.ChangeEvent<HTMLInputElement>) => {
        this.setState({[e.target.name]: e.target.value})

    }
    render () {
        const {plate} = this.state
        return (
            <div>
                <h1> Submit a request to Reserve space </h1> 
                <form>
                    <Stack spacing={3}>
                        <Input variant="outline" placeholder="Placa" width="500px" alignSelf="center" value={this.state.plate} onChange={this.changeHandler}/>
                        <Input variant="outline" placeholder="Name" width="500px" alignSelf="center" />
                        <Button type="submit" colorScheme="teal" variant="outline" width="100px" alignSelf="center">
                         Submit
                        </Button>
                    </Stack>
                </form>
            </div>
        )
    }
}

export default PostForm;
